export const getCategoryName = (category: WordCategory): string => {
  const names: Record<WordCategory, string> = {
    verbs: 'Глаголы',
    adjectives: 'Прилагательные',
    nouns: 'Существительные',
  };
  return names[category];
};

export const getCategoryEmoji = (category: WordCategory): string => {
  const emojis: Record<WordCategory, string> = {
    verbs: '🏃',
    adjectives: '🎨',
    nouns: '📦',
  };
  return emojis[category];
};

export const getWordsByCategory = (category: WordCategory): Word[] => {
  return words.filter(word => word.category === category);
};

export const shuffleArray = <T>(array: T[]): T[] => {
  const shuffled = [...array];
  for (let i = shuffled.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
  }
  return shuffled;
};
